import React from 'react';
import ReactDOM from 'react-dom';
const { URL, URLSearchParams } = require('url')


export class MyAdditionForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
firstName: '',
lastName: '',
bio: '',
};
}


  handleSubmit = (event) => {
event.preventDefault(); 
        const url = `http://localhost:3000/?firstName=:${this.state.firstName}&lastName=${this.state.lastName}&bio=${this.state.bio}#admin`
        fetch(url)
            .then((result) => result.json())
            .then(result => {
                alert('Form Submitted');
                this.setState({ queryResult: result.Addition })
            });
    }

submitForm = () => {
this.props.handleSubmit(this.state)
this.setState(this.state)
}

myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({[nam]: val});
}

  render() {
    return (
<div>
<div className="row"><div className="col">
    <form id="my-form" method="GET" handleSubmit="{this.handleSubmit}">
<div className="form-group">
        <h1>Hello user!</h1>
        <label htmlFor="lastName">Enter your first name</label>
        <input id="firstName" className="form-control" name="firstName" type="text" onChange={this.myChangeHandler} />
</div>
<div className="form-group">
        <label htmlFor="lastName">Enter your last name</label>
        <input id="lastName" className="form-control" name="lastName" type="text" onChange={this.myChangeHandler} />
</div>
<div className="form-group">
        <label htmlFor="bio">Enter your bio</label>
        <input id="bio" className="form-control" name="bio" type="text" onChange={this.myChangeHandler} />
</div>
        <input type="submit" value="submit" className="btn btn-primary" onClick={this.submitForm} />
      </form>
</div>
</div>

<div className="row">
                    <div className="col">
                        <h3>Your Result (from ReactJS) is: {this.state.firstName}, {this.state.lastName}; {this.state.bio} </h3>
                    </div>
                </div>
<div className="row">
                    <div className="col"><h3>Your Result (from server) is: {this.state.queryString} </h3>
                    </div>
                </div>

</div>
);
}
}
